import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function RestaurantsList() {
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/restaurants")
      .then((response) => response.json())
      .then((data) => setRestaurants(data));
  }, []);

  const buttonStyle = {
    paddingBottom: 20,
  };

  const linkStyle = {
    textDecoration: "none",
  };

  const listItemStyle = {
    backgroundColor: "transparent",
  };

  const restaurantLinkStyle = {
    color: "#fff",
    padding: "0.75rem",
  };

  const restaurantAddressStyle = {
    color: "#fff",
    padding: "0.75rem",
  };

  return (
    <div className="bg-primary text-white p-5">
      <div className="container">
        <h1 className="display-4 mb-4">Our Restaurants</h1>
        <div style={buttonStyle}>
          <button>
            <Link
              style={{ ...linkStyle, ...restaurantLinkStyle }}
              className="btn btn-light text-primary p-3"
              to="/restaurant_pizzas/new"
            >
              Add a new Pizza
            </Link>
          </button>
        </div>

        <ul className="list-group">
          {restaurants.map((restaurant) => (
            <li
              key={restaurant.id}
              style={listItemStyle}
              className="list-group-item"
            >
              <Link
                to={`/restaurants/${restaurant.id}`}
                style={{ ...linkStyle, ...restaurantLinkStyle }}
                className="restaurant-link"
              >
                {restaurant.name}
              </Link>
              <p style={restaurantAddressStyle} className="restaurant-address">
                {restaurant.address}
              </p>
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => handleDelete(restaurant.id)}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RestaurantsList;
