import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function RestaurantsList() {
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/restaurants")
      .then((response) => response.json())
      .then((data) => setRestaurants(data));
  }, []);

  function handleDelete(id) {
    fetch(`/restaurants/${id}`, {
      method: "DELETE",
    }).then((r) => {
      if (r.ok) {
        setRestaurants((restaurants) =>
          restaurants.filter((restaurant) => restaurant.id !== id)
        );
      }
    });
  }

  return (
    <div
      style={{
        display: "grid",
        placeItems: "center",
        minHeight: "100vh",
        fontFamily:
          '"SF Pro Text", "SF Pro Icons", "AOS Icons", "Helvetica Neue", Helvetica, Arial, sans-serif, system-ui',
        background:
          "linear-gradient(50deg, hsl(0 0% 50% / 0.25), transparent), hsl(0 0% 0%)",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          aspectRatio: "1",
          width: "200px",
          position: "absolute",
          left: "50%",
          bottom: "100%",
          transform: "translate(-50%, 0%)",
          animation:
            "scale var(--speed) calc(var(--delay, var(--speed) * -0.5)) infinite linear",
          filter: "url(#drop)",
        }}
      >
        {/* Add SVG filter with id="drop" here */}
      </div>

      <div style={{ containerType: "size", position: "relative", zIndex: "2" }}>
        <h1 style={{ fontSize: "2rem", color: "white" }}>Our Restaurants</h1>
        <div style={{ paddingBottom: 20 }}>
          <button>
            <Link
              style={{
                textDecoration: "none",
                color: "white",
                padding: "1rem 2rem",
                background: "#eee",
                borderRadius: "5px",
              }}
              to="/restaurant_pizzas/new"
            >
              Add a new Pizza
            </Link>
          </button>
        </div>

        <ul>
          {restaurants.map((restaurant) => (
            <li
              key={restaurant.id}
              style={{
                listStyle: "none",
                background: "transparent",
                padding: "1rem",
                margin: "1rem 0",
                border: "1px solid #eee",
                borderRadius: "5px",
              }}
            >
              <Link
                to={`/restaurants/${restaurant.id}`}
                style={{
                  textDecoration: "none",
                  color: "white",
                  padding: "1rem",
                }}
              >
                {restaurant.name}
              </Link>
              <p style={{ color: "white", padding: "1rem" }}>
                {restaurant.address}
              </p>
              <button
                type="button"
                style={{
                  background: "red",
                  color: "white",
                  padding: "0.5rem 1rem",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
                onClick={() => handleDelete(restaurant.id)}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RestaurantsList;
