import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function RestaurantsList() {
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/restaurants")
      .then((response) => response.json())
      .then((data) => setRestaurants(data));
  }, []);

  function handleDelete(id) {
    fetch(`/restaurants/${id}`, {
      method: "DELETE",
    }).then((r) => {
      if (r.ok) {
        setRestaurants((restaurants) =>
          restaurants.filter((restaurant) => restaurant.id !== id)
        );
      }
    });
  }

  return (
    <div
      style={{
        background: "#eee",
        color: "#000",
        fontFamily: "Helvetica Neue, Helvetica, Arial, sans-serif",
        fontSize: "14px",
        margin: 0,
        padding: 0,
        overflowX: "hidden",
        position: "relative",
        height: "100%",
      }}
    >
      <div
        style={{
          maxWidth: "50%",
          overflow: "visible",
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
        }}
      >
        <h1
          style={{
            fontSize: "2rem",
            fontFamily: '"Libre Baskerville", serif',
            position: "relative",
          }}
        >
          Our Restaurants
        </h1>
        <div style={{ paddingBottom: 20 }}>
          <button>
            <Link
              to="/restaurant_pizzas/new"
              style={{
                background: "#eee",
                color: "#000",
                padding: "1rem",
                textDecoration: "none",
              }}
            >
              Add a new Pizza
            </Link>
          </button>
        </div>

        <ul
          style={{
            listStyle: "none",
            padding: 0,
          }}
        >
          {restaurants.map((restaurant) => (
            <li
              key={restaurant.id}
              style={{
                background: "transparent",
                margin: "0",
                padding: 0,
              }}
            >
              <Link
                to={`/restaurants/${restaurant.id}`}
                style={{
                  color: "#eee",
                  padding: "1rem",
                  display: "block",
                  textDecoration: "none",
                }}
              >
                {restaurant.name}
              </Link>
              <p
                style={{
                  color: "#eee",
                  padding: "1rem",
                }}
              >
                {restaurant.address}
              </p>
              <button
                type="button"
                style={{
                  background: "#e66465",
                  color: "#fff",
                  padding: "0.5rem",
                  border: "none",
                  cursor: "pointer",
                }}
                onClick={() => handleDelete(restaurant.id)}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RestaurantsList;
